data_x = [17.7876    4.4820   23.8285   -0.5855   24.2197    8.5787   10.5677    8.8718   -0.3771];data_y = [65.754   41.825   78.419   33.624   83.678   49.482   60.369   54.087   34.140];c_i = randi(10);m_i = 0eata = 0.1;c = [];m = [];rss = [];N = size(data_x,2);i=0;stop = 0;count = 1;ideal_c = c_i;ideal_m = m_i;while(stop==0)  der_c1 = find_derivative_of_rss_to_c(data_y,data_x,m_i,c_i,N);  der_m1 = find_derivative_of_rss_to_m(data_y,data_x,m_i,c_i,N);     c1 = c_i - (eata*der_c);  m1 = m_i - (eata*der_m);    der_c2 = find_derivative_of_rss_to_c(data_y,data_x,m1,c1,N);  der_m2 = find_derivative_of_rss_to_m(data_y,data_x,m1,c1,N);    fprintf("der c1 %d der c2 = %d\nder_m1 = %d der m2 = %d\n",der_c1,der_c2,der_m1,der_m2);     if abs(der_c1)<abs(der_c2) || abs(der_c1)<abs(der_c2) || (der_c2>0 && der_c1<0) || (der_c1>0 && der_c2<0) || (der_m2>0 && der_m1<0) || (der_m1>0 && der_m2<0)    eata = eata*0.8;  else    stop = 1;  endif    if eata<0.01    stop = 1;  endif
endfprintf("Eata %d\n",eata);stop = 0;while(stop==0)  count = count+1;  der_c = find_derivative_of_rss_to_c(data_y,data_x,m_i,c_i,N);  der_m = find_derivative_of_rss_to_m(data_y,data_x,m_i,c_i,N);   
  fprintf("step_c = %d and step_m = %d",der_c,der_m);  if count>40 || (abs(der_c)<0.1 || abs(der_m)<0.1 && count>2)    stop=1;    fprintf("Terminated");    ideal_c = c_i;    ideal_m = m_i;    fprintf("Optimal m is : %d\nOptimal c is : %d\n",ideal_m,ideal_c);
  endif  c_i = c_i - (eata*der_c);  m_i = m_i - (eata*der_m);  c(end+1) = c_i;  m(end+1) = m_i;  new_err = find_err(data_y,m_i,data_x,c_i);  disp("ERR ");  disp(new_err);  i++; endA = meshgrid(m,c);Z = find_rss_for_m_c(data_y,m,data_x,c,N);%disp(Z);disp("MinIntensityValue ");minIntensityValue = min(Z(:));disp(minIntensityValue);contour(m,c,Z);[~, minIdx] = min(Z(:)); [row,col] = ind2sub(size(Z),minIdx); xMin = m(col); yMin = c(row); disp(xMin);disp(yMin);%hold on %plot(xMin, yMin, 'rx');%hold on%plot(m,c,Z);fprintf("Optimal m is : %d\nOptimal c is : %d\n",xMin,yMin);